package goimgur

import "testing"

func TestGetFile(t *testing.T) {
	type getFileTest struct {
		Path   string
		Base64 string
	}

	getFileTests := []getFileTest{
		{"", ""},
		{"test_data/image_test.jpg", ""},
	}

	for _, td := range getFileTests {
		// I don't care about error, input -> output mapping should be expected
		base64, _ := getFile(td.Path)
		if base64 != td.Base64 {
			t.Errorf("TestGetFile: Return values from getFile is not expected: Expected: %s, Returned: %s", td.Base64, base64)
		}
	}

}

func TestUploadImage(t *testing.T) {
	_ = uploadImage("test_data/image_test.jpg")
}
